import React from 'react';
import logo from './logo.svg';
import './App.css';




class App extends React.Component {
  quotations = [
    "Rien ne sert de courir, il faut partir à point",
    "La raison du plus fort est toujours la meilleure",
    "La douleur est toujours moins forte que la plainte",
    "En toute chose, il faut considérer la fin",
    "On hasarde de perdre en voulant trop gagner"
  ];


    constructor(props){
    super(props);

    this.state = { 
      letters: ['A', 'b', 'c', 'd', 'e', 'f', 'g', 'h', 'i', 'j', 'k', 'l', 'm',
              'n', 'o', 'p', 'q', 'r', 's', 't', 'u', 'v', 'w', 'x', 'y', 'z'],

    };
  }


  // Produit une représentation textuelle de l’état de la partie,
// chaque lettre non découverte étant représentée par un _underscore_.
// (CSS assurera de l’espacement entre les lettres pour mieux
// visualiser le tout).
computeDisplay(phrase, usedLetters) {
  return phrase.replace(/\w/g,
    (letter) => (usedLetters.includes(letter) ? letter : '_')
  )
}

setBackgroundColor(index){
  return this.state.letters[index] === this.state.letters[index].toUpperCase() ? "rgb(185, 185, 185)" : "rgb(240, 240, 240)"
}

tryLetter(index){
  if(this.state.letters[index] === this.state.letters[index].toLowerCase() ){
    let newLetters = this.state.letters;
    newLetters[index] = newLetters[index].toUpperCase(); 
    this.setState({
      letters:  newLetters,
    });
  }
}

showRowButtons(row){
  return <div style={{
    "display": "flex",
    "justifyContent": "center",
    "alignItems": "center",
    "backgroundColor": "#222"
    }}>

      {this.state.letters.map((letter, index) => {
        return row === 1 ?  
        (index < 13 ? <button key={index} style={{"width": 25, "backgroundColor": this.setBackgroundColor(index)}} onClick={this.tryLetter(index)}>{letter.toUpperCase()}</button> : '')
        : (index >= 13 ? <button key={index} style={{"width": 25, "backgroundColor": this.setBackgroundColor(index)}}>{letter.toUpperCase()}</button> : '')
      })
    }
  </div>
}

  render(){
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            {this.computeDisplay(this.quotations[0], 'a')}
          </p>
         {this.showRowButtons(1)} 
         {this.showRowButtons(2)} 


        </header>
      </div>
    );
  }
}

export default App;
