              <div>Bravo, vous avez gagn√© en {guesses} coups.
                <br /><button onClick={()=>newGame()}>Rejouer</button></div>
            </div>
            :
            <div className="Container">
                {showRowButtons(1)} 
                {showRowButtons(2)}
                <br />Nombre de coups : {guesses}
            </div> }

        </div>
)


export default Game
